export default {
  name: "num-picker",
  props: {
    value: {
      type: Number
    },
    options: {
      type: Array
    }
  },
  data() {
    return {
      optionChanged: false
    };
  },
  computed: {
    localValue: {
      get() {
        return this.value;
      },
      set(val) {
        if (!this.optionChanged) {
          this.$emit('input', val);
        }
      }
    }
  },
  watch: {
    options() {
      this.optionChanged = true;
      setTimeout(() => {
        this.optionChanged = false;
      }, 300);
    }
  }
};
